import React from "react";
import { Bar } from "react-chartjs-2";
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

function Metrix({metrix_data}){
    console.log(metrix_data)
    return(
        <div>
            {metrix_data.map(matrix =>(<div key={matrix.request_id} style={{ maxWidth: "30%", "textAlign": "center", margin: "0 auto" }}>
                <Bar
                    data={{
                        // Name of the variables on x-axies for each bar
                        labels: matrix["MetricDataResults"][0]["Timestamps"],
                        datasets: [
                        {
                            // Label for bars
                            label: "FreeStorageSpace/datetime",
                            // Data or value of your each variable
                            data: matrix["MetricDataResults"][0]["Values"],
                            borderWidth: 0.1,
                            borderColor: ["black"],
                            backgroundColor: ["grey"]
                        },
                        ],
                        options: {
                            scales: {
                                xAxes: [{
                                    barThickness: 6,  // number (pixels) or 'flex'
                                    maxBarThickness: 8 // number (pixels)
                                }]
                            }
                        }
                    }}
                    // Height of graph
                    height={300}
                />

            </div>))}
        </div>
    )
}

export default Metrix
